" <newline>+                      "</form><br/>
", <newline>+                      escaped_command.c_str(), <newline>+                      title.c_str()); <newline> } <newline> }  // namespace <newline>@@ -173,7 +164,6 @@ void ViewBlobInternalsJob::GenerateHTML(std::string* out) const { <newline>        ++iter) { <newline>     AddHTMLBoldText(iter->first, out); <newline>     AddHTMLButton(kRemove, iter->first, out); <newline>-    out->append("<br/>"); <newline>     GenerateHTMLForBlobData(*iter->second, out); <newline>   } <newline> }

